mapper.select:
select

from user_article_collection


    and blog_id=#{blogId,jdbcType=BIGINT}


    and user_id=#{userId,jdbcType=BIGINT}

mapper.update:
update user_article_collection
set collection_status=1,collection_active=1


    and blog_id=#{blogId,jdbcType=BIGINT}


    and user_id=#{userId,jdbcType=BIGINT}

update user_article_collection
set collection_status=0,collection_active=0


    and blog_id=#{blogId,jdbcType=BIGINT}


    and user_id=#{userId,jdbcType=BIGINT}
