mapper.select:
select

from user_article_thump


        and blog_id=#{blogId,jdbcType=BIGINT}


        and user_id=#{userId,jdbcType=BIGINT}

select count(id) AS total
from user_article_thump
where  thump_active=1 AND thump_status=1 AND blog_id=#{blogId,jdbcType=BIGINT}

mapper.update:
update user_article_thump
set thump_status=0,thump_active=0


        and blog_id=#{blogId,jdbcType=BIGINT}


        and user_id=#{userId,jdbcType=BIGINT}

mapper.delete:
delete from user_article_thump


        and blog_id=#{blogId,jdbcType=BIGINT}


        and user_id=#{userId,jdbcType=BIGINT}

mapper.update:
update user_article_thump
set thump_status=1,thump_active=1


        and blog_id=#{blogId,jdbcType=BIGINT}


        and user_id=#{userId,jdbcType=BIGINT}
