mapper.insert:

insert into user_member (member_id, member_vip, member_svip,
rechargetime_vip, expiretime_vip, rechargetime_svip,
expiretime_svip, createtime, updatetime
)
values (#{memberId,jdbcType=BIGINT}, #{memberVip,jdbcType=INTEGER}, #{memberSvip,jdbcType=INTEGER},
#{rechargetimeVip,jdbcType=DATE}, #{expiretimeVip,jdbcType=DATE}, #{rechargetimeSvip,jdbcType=DATE},
#{expiretimeSvip,jdbcType=DATE}, #{createtime,jdbcType=TIMESTAMP}, #{updatetime,jdbcType=TIMESTAMP}
)

mapper.update:
update user_member
set expiretime_vip=#{updatedExpiretimeVip,jdbcType=DATE}
where 1=1

  and member_id=#{memberId,jdbcType=BIGINT}

mapper.select:
select
from user_member where 1=1
and member_id=#{memberId,jdbcType=BIGINT}

select member_svip
from user_member


    and member_id=#{memberId,jdbcType=BIGINT}

select expiretime_svip
from user_member


    and member_id=#{memberId,jdbcType=BIGINT}

select member_id
from user_member
where member_vip=1 and DATEDIFF(expiretime_vip,now())=-1;

select member_id
from user_member
where member_svip=1 and DATEDIFF(expiretime_svip,now())=-1;

mapper.update:
update user_member
set member_vip=0


    and member_id=#{memberId,jdbcType=BIGINT}

update user_member
set member_svip=0


    and member_id=#{memberId,jdbcType=BIGINT}
