mapper.select:
select RealName
from user_security
where Username=#{username,jdbcType=VARCHAR}

mapper.update:
update user_wallet
set wallet_password=#{walletPassword,jdbcType=VARCHAR}  , realname=#{updatedRealname,jdbcType=VARCHAR}
where 1=1

  and wallet_id=#{walletId,jdbcType=BIGINT}

update user_wallet
set wallet_password=#{updatedWalletPassword,jdbcType=VARCHAR}
where 1=1

  and wallet_id=#{walletId,jdbcType=BIGINT}

mapper.select:
select
from user_wallet where 1=1
and wallet_id=#{walletId,jdbcType=BIGINT}

select id
from user_wallet


        and wallet_id=#{walletId,jdbcType=BIGINT}
