mapper.insert:

insert into user_continuereward (continue_id, continueReward_one, continueReward_two, 
  continueReward_three, continueReward_four, 
  continueReward_five, continueReward_six, 
  continueReward_seven, firstTime, lastTime
  )
values (#{continueId,jdbcType=BIGINT}, #{continuerewardOne,jdbcType=INTEGER}, #{continuerewardTwo,jdbcType=INTEGER}, 
  #{continuerewardThree,jdbcType=INTEGER}, #{continuerewardFour,jdbcType=INTEGER}, 
  #{continuerewardFive,jdbcType=INTEGER}, #{continuerewardSix,jdbcType=INTEGER}, 
  #{continuerewardSeven,jdbcType=INTEGER}, #{firsttime,jdbcType=TIMESTAMP}, #{lasttime,jdbcType=TIMESTAMP}
  )

mapper.select:
select
from user_continuereward where 1=1
and continue_id=#{continueId,jdbcType=BIGINT}
